export function consoleLog(n,t){t&&console.log(n)}export function addJs(n,t){const i=document.createElement("script");i.src=n;i.type="text/javascript";i.async=!0;t==0?document.head.appendChild(i):document.body.appendChild(i);consoleLog("js {url} added to ({appendTo})")}export function addCss(n,t){document.addEventListener("DOMContentLoaded",function(){var i=document.createElement("link");i.rel="stylesheet";i.href=n;t==0?document.head.appendChild(i):document.body.appendChild(i);consoleLog("css {url} added to ({appendTo})")})}export function addClassById(n,t,i){document.getElementById(n).classList.add(t);consoleLog("class {classes} added to {elId}",i)}export function remClassById(n,t,i){document.getElementById(n).classList.remove(t);consoleLog("class {classes} removed {elId}",i)}export function addClassToBody(n,t){document.body.classList.add(n);consoleLog("class {classes} added to body",t)}export function remClassFromBody(n,t){document.body.classList.remove(n);consoleLog("class {classes} added to body",t)}